mapper.insert:

	SELECT NVL(MAX(book_no), 0) + 1
	FROM book_tbl_06

INSERT INTO book_tbl_06(
						    book_no,
						    book_name,
						    book_coverimg,
						    book_date,
						    book_price,
						    book_publisher,
						    book_info
						)
		VALUES 			(
							#{bookNo}
							, #{bookName}
							, #{bookCoverImg}
							, #{bookDate}
							, #{bookPrice}
							, #{bookPublisher}
							, #{bookInfo}
						)

mapper.update:
UPDATE book_tbl_06
SET book_no = #{bookNo}
	, book_name = #{bookName}
	, book_coverimg = #{bookCoverImg}
	, book_date = #{bookDate}
	, book_price = #{bookPrice}
	, book_publisher = #{bookPublisher}
	, book_info = #{bookInfo}
WHERE book_no = #{bookNo}
