mapper.insert:

	SELECT NVL(MAX(department_id), 0) + 1
	FROM departments

INSERT INTO departments
			(
			department_id
			, department_name
			, manager_id
			, location_id
			)
VALUES 		(
			#{departmentId}
			, #{departmentName}
			, #{managerId}
			, #{locationId}
			)

mapper.update:
UPDATE departments
SET department_name = #{dept.departmentName}
		, manager_id = #{dept.managerId}
		, location_id = #{dept.locationId}
WHERE department_id = #{id}
