mapper.insert:

	SELECT NVL(MAX(empid), 0) + 1
	FROM emp

INSERT INTO emp
	       (
	        empid
	        , empname
	        , mgr
	        , sal

	        , deptid

	        , hiredate
	       )
	VALUES
		   (

			#{empid}
			, #{empname}
			, #{mgr}
			, #{sal}

			, #{deptid}



					, #{hiredate}						


					, sysdate


		   )

mapper.update:
UPDATE emp
SET empname = #{emp.empname}
		, sal = #{emp.sal}
		, mgr = #{emp.mgr}
		, deptid = #{emp.deptid}
WHERE empid = #{id}
